        id: Date.now().toString() + Math.random().toString(36).substr(2, 9), // Ð‘Ð¾Ð»ÐµÐµ ÑƒÐ½Ð¸ÐºÐ°Ð»ÑŒÐ½Ñ‹Ð¹ ID
        file: file,
