          await loadPhotos(); // ÐžÐ±Ð½Ð¾Ð²Ð»ÑÐµÐ¼ ÑÑ‚Ð°Ñ‚ÑƒÑ Ñ Ð¾ÑˆÐ¸Ð±ÐºÐ¾Ð¹
        }
