import React from 'react';

const PhotoGrid = ({ photos, onRetryUpload, isOnline }) => {
  if (photos.length === 0) {
    return (
      <div style={{ textAlign: 'center', padding: '40px', color: '#666' }}>
        <p>üì∑ –ü–æ–∫–∞ –Ω–µ—Ç –∑–∞–≥—Ä—É–∂–µ–Ω–Ω—ã—Ö —Ñ–æ—Ç–æ–≥—Ä–∞—Ñ–∏–π</p>
        <p>–î–æ–±–∞–≤—å—Ç–µ –ø–µ—Ä–≤—É—é —Ñ–æ—Ç–æ–≥—Ä–∞—Ñ–∏—é, –ø–µ—Ä–µ—Ç–∞—â–∏–≤ —Ñ–∞–π–ª –≤ –æ–±–ª–∞—Å—Ç—å –≤—ã—à–µ</p>
      </div>
    );
  }

  const getStatusText = (status) => {
    switch (status) {
      case 'pending':
        return '–û–∂–∏–¥–∞–µ—Ç –æ—Ç–ø—Ä–∞–≤–∫–∏';
      case 'uploaded':
        return '–û—Ç–ø—Ä–∞–≤–ª–µ–Ω–æ';
      case 'error':
        return '–û—à–∏–±–∫–∞ –æ—Ç–ø—Ä–∞–≤–∫–∏';
      default:
        return '–ù–µ–∏–∑–≤–µ—Å—Ç–Ω–æ';
    }
  };

  const getStatusClass = (status) => {
    switch (status) {
      case 'pending':
        return 'status-pending';
      case 'uploaded':
        return 'status-uploaded';
      case 'error':
        return 'status-error';
      default:
        return 'status-pending';
    }
  };

  const formatFileSize = (bytes) => {
    if (bytes === 0) return '0 Bytes';
    const k = 1024;
    const sizes = ['Bytes', 'KB', 'MB', 'GB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];
  };

  const formatDate = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleString('ru-RU');
  };

  return (
    <div className="photo-grid">
      {photos.map((photo) => (
        <div key={photo.id} className="photo-item">
          <img
            src={photo.url || URL.createObjectURL(photo.file)}
            alt={photo.name}
            onError={(e) => {
              e.target.src = 'data:image/svg+xml;base64,';
            }}
          />
          <div className="photo-info">
            <div className="photo-name" title={photo.name}>
              {photo.name}
            </div>
            <div style={{ fontSize: '12px', color: '#666', marginBottom: '8px' }}>
              {formatFileSize(photo.size)} ‚Ä¢ {formatDate(photo.createdAt)}
            </div>
            <div className={`photo-status ${getStatusClass(photo.status)}`}>
              {getStatusText(photo.status)}
            </div>
            {photo.status === 'error' && isOnline && (
              <button
                className="retry-button"
                onClick={() => onRetryUpload(photo.id)}
                title="–ü–æ–≤—Ç–æ—Ä–∏—Ç—å –æ—Ç–ø—Ä–∞–≤–∫—É"
              >
                üîÑ –ü–æ–≤—Ç–æ—Ä–∏—Ç—å
              </button>
            )}
            {photo.status === 'pending' && !isOnline && (
              <div style={{ fontSize: '12px', color: '#856404', marginTop: '5px' }}>
                ‚è≥ –û–∂–∏–¥–∞–µ—Ç –∏–Ω—Ç–µ—Ä–Ω–µ—Ç–∞
              </div>
            )}
          </div>
        </div>
      ))}
    </div>
  );
};

export default PhotoGrid;
